mapper.update:
UPDATE board
SET    title=#{title},
       reg_date=#{reg_date},
       reg_user=#{reg_user},
       content=#{content}
WHERE  seq = #{seq}

mapper.select:
select CRT_CD ,CRT_NM ,CRT_SSTS ,CRT_DTM ,CRT_CNT from crtprac where 1=1
AND CRT_SSTS = #{crt_ssts}
AND CRT_CD like CONCAT('%',#{crt_cd},'%')

mapper.update:
UPDATE crtprac 
SET    CRT_NM=#{CRT_NM},
       CRT_SSTS=#{CRT_SSTS},
       CRT_DTM=#{CRT_DTM}
WHERE  CRT_CD = #{CRT_CD}

mapper.select:
select COUNT(*) AS CNT
  from member
  where member_id = #{id}

	select COUNT(*) as CNT
       ,member_id as ID
	 from member
	 where member_id = #{id}
	 and password = #{pw}
